    /***************************************************************************
     * OBJECT DIAGRAM CLASS
     ***************************************************************************/
    
    var objectdiagram = Joint.dia.BAObjectDiagram = {};
    
    ////////////////////////////////////////////////////////////////////////////
    // WINDOWS
    
    ////////////////////////////////////////////////////////////////////////////
    ////////////////////////////////////////////////////////////////////////////
    
    ////////////////////////////////////////////////////////////////////////////
    // ELEMENTS
    
    objectdiagram.Instance = Element.extend({
        object: "Instance",
        module: "BAObjectDiagram",
        
        data:{
            name: 'Instance',
            refClassifier: 'ClassName',
            attributes: []                                                      // attributes of 
        },
    
        init: function(properties){
        
            var p = Joint.DeepSupplement(this.properties, properties, {
                attrs: {
                    fill:"#fff",
                    'text-decoration': 'underline'
                },
                
                swimlineAttrs: {
                    fill: '#000000'  
                },
                
                nameOffsetY: 4,
                swimlaneOffsetY: 10,
            
                data:{
                    name: this.data.name,
                    refClassifier: this.data.refClassifier
                }
            
            });
            
            // wrapper
            this.setWrapper(w = this.paper.rect(p.rect.x, p.rect.y, p.rect.width, p.rect.height).attr(p.attrs));    
            // inner
            this.addInner(this.getLabelElement());
            this.addInner( this.getSwimlane() );
            
        },
        // Returns minimal size of Element. Used for resizing. (Opi)
        getMinSize: function(){
            // label
            var w = this.inner[0].getBBox()["width"] + 2*this.properties.labelOffsetX;
            var h = this.inner[0].getBBox()["height"] + this.properties.labelOffsetY;

            return [w, h];
        },
        
        getLabelElement: function(){
            var
            p = this.properties,
            bb = this.wrapper.getBBox(),
            t = this.paper.text(bb.x, bb.y, p.data.name + '/' + p.data.refClassifier).attr(p.labelAttrs || {}),            
            tbb = t.getBBox();
            t.translate(bb.width/2 - tbb.width/2 + bb.x - tbb.x, bb.y - tbb.y + p.nameOffsetY);            
            return t;
        },
        
        getSwimlane: function(){
            
            var p = this.properties,
            bb = this.wrapper.getBBox();
            
            return this.paper.path(["M", bb.x, bb.y + p.nameOffsetY + p.swimlaneOffsetY * 2, "L", bb.x + bb.width, bb.y + p.nameOffsetY  +  2 * p.swimlaneOffsetY].join(" "));
        },
        
        zoom: function(){
            this.update();
        },
    
        update: function(){                        
            this.inner[0].remove(); // label
            this.inner[1].remove(); // label
            this.inner[0] = this.getLabelElement();    
            this.inner[1] = this.getSwimlane();    
            
            
            this.inner[0].wholeShape = this;
            this.inner[0].parentElement = this;
            if (this.inner[0]._isElement) this.inner[0].properties.parent = this.euid();
           
            if (!this.inner[0]._isElement && this._opt && this._opt.draggable){
                this.inner[0].mousedown(this.dragger);
                this.inner[0].node.style.cursor = "move";
            }
            
            this.inner[1].wholeShape = this;
            this.inner[1].parentElement = this;
            if (this.inner[1]._isElement) this.inner[0].properties.parent = this.euid();
           
            if (!this.inner[1]._isElement && this._opt && this._opt.draggable){
                this.inner[1].mousedown(this.dragger);
                this.inner[1].node.style.cursor = "move";
            }
            
        }
    
    });
    
