    ////////////////////////////////////////////////////////////////////////////
    // ELEMENTS
    
    deploymentdiagram.Node = Element.extend({
        object: "Node",
        module: "BADeploymentDiagram",
        
        data:{
            name: 'NodeName',            
            attributes: []                                                      // attributes of             
        },
    
        init: function(properties){                   
        
            var p = Joint.DeepSupplement(this.properties, properties, {
                attrs: {
                    fill:"#ffffff",
                    stroke: 'none'                    
                },
                
                swimlineAttrs: {
                    fill: '#000000'  
                },
                
                nameOffsetY: 14,
                swimlaneOffsetY: 10,
            
                data:{
                    name: this.data.name                    
                }
            
            });
            
            // wrapper
            this.setWrapper(w = this.paper.rect(p.rect.x, p.rect.y, p.rect.width, p.rect.height).attr(p.attrs));    
            // inner
            
            this.addInner( this.getElementPath() );
            this.addInner(this.getLabelElement());
        },
        // Returns minimal size of Element. Used for resizing. (Opi)
        getMinSize: function(){
            // label
            var w = this.inner[0].getBBox()["width"] + 2*this.properties.labelOffsetX;
            var h = this.inner[0].getBBox()["height"] + this.properties.labelOffsetY;

            return [w, h];
        },
        
        getLabelElement: function(){
            
            var stereotype = this.data.stereotype,
            stereotypeLabel = "";
            
            if(stereotype != ''){
                stereotypeLabel = "<<"+stereotype +">>\n";
            }
            
            var
            p = this.properties,
            bb = this.wrapper.getBBox(),
            t = this.paper.text(bb.x, bb.y, stereotypeLabel + this.data.name).attr(p.labelAttrs || {}),            
            tbb = t.getBBox();
            t.translate(bb.width/2 - tbb.width/2 + bb.x - tbb.x, bb.y - tbb.y + p.nameOffsetY);            
            return t;
        },
        
        getElementPath: function(){
            
            var p = this.properties,
            bb = this.wrapper.getBBox(),
            
            icon = this.paper.path(
                "M " + bb.x + " " + (bb.y + 10) + " l " + (bb.width - 10) + " 0 l 0 " + (bb.height - 10) + " l -"+ (bb.width - 10) +" 0 l 0 -" + ( bb.height - 10 ) + " z "  +
                "M " + bb.x + " " + (bb.y + 10) + " l 10 -10 l " +  (bb.width - 10) + " 0 l -10 10 l -"+ (bb.width - 10) +" 0 z " + 
                "M " + (bb.x + bb.width - 10) + " " + (bb.y + bb.height) + " l 10 -10 l 0 -" + (bb.height - 10) + " l -10 10 z"
                ).attr({
                stroke: 'black', 
                fill: 'white', 
                'stroke-width': 1
            });
            
            return icon;
            
        },
        
        zoom: function(){
            this.update();
        },
    
        update: function(){                        
            this.inner[0].remove(); // label
            this.inner[1].remove(); // label            
            this.inner[0] = this.getElementPath();
            this.inner[1] = this.getLabelElement();    
            
            
            
            
            for( var i = 0; i < 2; i++){
                this.inner[i].wholeShape = this;
                this.inner[i].parentElement = this;
                if (this.inner[i]._isElement) this.inner[i].properties.parent = this.euid();

                if (!this.inner[i]._isElement && this._opt && this._opt.draggable){
                    this.inner[i].mousedown(this.dragger);
                    this.inner[i].node.style.cursor = "move";
                }
            }
            
        }
    
    });
    
